mapper.select:
select count(*) from ( select rrpdc.domain_name,rrpdc.company_short_name,rrpdc.website_app_name from (select rrpdc.domain_name,rrpdc.company_short_name,rrpdc.website_app_name from ${queryParam.queryTable} as rrpdc
group by rrpdc.domain_name,rrpdc.company_short_name,rrpdc.website_app_name
limit #{queryParam.rankNumber}
) as rrpdc left join (select domain_name from dim.dim_dial_domain_record group by domain_name) as dddr on rrpdc.domain_name = dddr.domain_name
and (dddr.domain_name is not null and dddr.domain_name != '')
and (dddr.domain_name is null or dddr.domain_name = '')
)

select rrpdc.*, dddr.domain_name as dial_domain_name, if(dial_domain_name is null or dial_domain_name = '', '未下发', '已下发') as distribute_state from (select rrpdc.domain_name,rrpdc.company_short_name,rrpdc.website_app_name, arrayStringConcat(groupArray(distinct rrpdc.cname), ';') as cname, sum(parse_total_cnt) as parse_total_cnt, sum(parse_success_cnt) as parse_success_cnt, sum(net_out_parse_flow_total) as net_out_parse_flow_total, sum(net_out_parse_total_cnt) as net_out_parse_total_cnt, sum(net_in_parse_total_cnt) as net_in_parse_total_cnt, sum(within_parse_total_cnt) as within_parse_total_cnt, sum(without_parse_total_cnt) as without_parse_total_cnt from ${queryParam.queryTable} as rrpdc
group by rrpdc.domain_name,rrpdc.company_short_name,rrpdc.website_app_name order by net_out_parse_flow_total desc
limit #{queryParam.rankNumber}
) as rrpdc left join (select domain_name from dim.dim_dial_domain_record group by domain_name) as dddr on rrpdc.domain_name = dddr.domain_name
and (dddr.domain_name is not null and dddr.domain_name != '')
and (dddr.domain_name is null or dddr.domain_name = '')
order by ${queryParam.orderBy}
limit #{queryParam.limit}
offset #{queryParam.offset}

select domain_name from ${queryParam.queryTable} as rrpdc
group by domain_name order by sum(net_out_parse_flow_total) desc
limit #{queryParam.rankNumber}
