mapper.select:
select answer_first_isp
from ${queryParam.queryTable}



        and website_app_name global in (
        select
        website_app_name
        from
        ${queryParam.queryTable}



        group by
        website_app_name
        order by sum(parse_total_cnt) desc limit #{queryParam.rankNumber}
        )


group by answer_first_isp
order by sum(a_record_parse_total_cnt) desc

select count(1) from ( select domain_name, answer_first_isp from ${queryParam.queryTable}
and website_app_name global in ( select website_app_name from ${queryParam.queryTable}
group by website_app_name order by sum(parse_total_cnt) desc limit #{queryParam.rankNumber} )
group by domain_name, answer_first_isp )

select domain_name, answer_first_isp, sum(parse_total_cnt) as parse_total_cnt, sum(a_record_parse_total_cnt) as a_record_parse_total_cnt, sum(net_out_parse_total_cnt) as net_out_parse_total_count from ${queryParam.queryTable}
and website_app_name global in ( select website_app_name from ${queryParam.queryTable}
group by website_app_name order by sum(parse_total_cnt) desc limit #{queryParam.rankNumber} )
group by domain_name, answer_first_isp order by net_out_parse_total_count desc, parse_total_cnt desc limit #{queryParam.offset}, #{queryParam.limit}

select count(1) from ( select answer_first_ip, answer_first_province, answer_first_city, answer_first_isp from ${queryParam.queryTable}
and website_app_name global in ( select website_app_name from ${queryParam.queryTable}
group by website_app_name order by sum(parse_total_cnt) desc limit #{queryParam.rankNumber} )
group by answer_first_ip, answer_first_province, answer_first_city, answer_first_isp )

select answer_first_ip, answer_first_province, answer_first_city, answer_first_isp, sum(parse_total_cnt) as parse_total_cnt from ${queryParam.queryTable}
and website_app_name global in ( select website_app_name from ${queryParam.queryTable}
group by website_app_name order by sum(parse_total_cnt) desc limit #{queryParam.rankNumber} )
group by answer_first_ip, answer_first_province, answer_first_city, answer_first_isp order by parse_total_cnt desc limit #{queryParam.offset}, #{queryParam.limit}

mapper.sql:
and net_out_parse_total_cnt > 0
and parse_time >= #{queryParam.startTime}
and parse_time < #{queryParam.endTime}
and website_app_name = #{queryParam.websiteAppName}
and website_type = #{queryParam.websiteType}
and answer_first_ip = #{queryParam.answerFirstIp}
and answer_first_province = #{queryParam.answerFirstProvince}
and answer_first_city = #{queryParam.answerFirstCity}
and answer_first_isp = #{queryParam.answerFirstIsp}
and domain_name = #{queryParam.domainName}
and user_type = #{queryParam.userType}
and districts_code = #{queryParam.districtsCode}
and country = #{queryParam.country}
and province = #{queryParam.province}
and city = #{queryParam.city}
and district = #{queryParam.district}
and isp = #{queryParam.isp}
and isp_code = #{queryParam.ispCode}
and server_node_name = #{queryParam.serverNodeName}

and net_out_parse_total_cnt > 0
and parse_time >= #{queryParam.startTime}
and parse_time < #{queryParam.endTime}
and website_app_name ilike concat('%',#{queryParam.websiteAppName},'%')
and website_type = #{queryParam.websiteType}
and answer_first_ip = #{queryParam.answerFirstIp}
and answer_first_province = #{queryParam.answerFirstProvince}
and answer_first_city = #{queryParam.answerFirstCity}
and answer_first_isp = #{queryParam.answerFirstIsp}
and domain_name = #{queryParam.domainName}
and user_type = #{queryParam.userType}
and districts_code = #{queryParam.districtsCode}
and country = #{queryParam.country}
and province = #{queryParam.province}
and city = #{queryParam.city}
and district = #{queryParam.district}
and isp = #{queryParam.isp}
and isp_code = #{queryParam.ispCode}
and server_node_name = #{queryParam.serverNodeName}

and website_app_name global in (
    select website_app_name from (
        select *
        from (
        select website_app_name,
        website_type,
        sum(parse_total_cnt) as parse_total_cnt,
        sum(a_record_parse_total_cnt) as a_record_parse_total_cnt,
        sum(parse_success_cnt) as parse_success_cnt,
        sum(net_in_parse_total_cnt) as net_in_parse_total_cnt,
        sum(net_out_parse_total_cnt) as net_out_parse_total_cnt,
        sum(within_parse_total_cnt) as within_parse_total_cnt,
        sum(without_parse_total_cnt) as without_parse_total_cnt,
        sum(cdn_parse_total_cnt) as cdn_parse_total_cnt,
        sum(idc_parse_total_cnt) as idc_parse_total_cnt,
        row_number() over (order by parse_total_cnt desc) as rank_number
        from ${domainWebsiteDetail.queryTable}



        group by website_app_name,website_type

            having

                if(a_record_parse_total_cnt == 0, 0.0, divide(net_in_parse_total_cnt * 100, a_record_parse_total_cnt)) >= #{domainWebsiteDetail.netInRateMin}

                    and



                if(a_record_parse_total_cnt == 0, 0.0, divide(net_in_parse_total_cnt * 100, a_record_parse_total_cnt)) <= #{domainWebsiteDetail.netInRateMax}


        settings allow_experimental_window_functions = 1
        )


                and rank_number <= #{domainWebsiteDetail.rankNumber}


        order by ${domainWebsiteDetail.orderBy}

            limit #{domainWebsiteDetail.limit}

                offset #{domainWebsiteDetail.offset}


    )
)
